//type alias interface는 = 을 지우면 됨
interface Store {
  currentPage: number;
  feeds: NewsFeed[]; // 생성한 newsFeed라는 형식이 들어가는 배열이라는 뜻
}
interface News {
  readonly id: number;
  readonly time_ago: number;
  readonly title: string;
  readonly url: string;
  readonly user: string;
  readonly content: string;
}

interface NewsFeed extends News {
  readonly comments_count: number;
  readonly points: number;
  read?: boolean; // optional 속성
}

interface NewsDetail extends News {
  readonly comments: NewsComment[];
}

interface NewsComment extends News {
  readonly comments: NewsComment[];
  readonly level: number;
}

const container: HTMLElement | null = document.getElementById('root'); //id값이 root를 불러옴
const ajax: XMLHttpRequest = new XMLHttpRequest();
const content = document.createElement('div');
const NEWS_URL = 'https://api.hnpwa.com/v0/news/1.json';
const CONTENT_URL = 'https://api.hnpwa.com/v0/item/@id.json';
// 공유되는 값을 객체로 묶어줌
const store: Store = {
  currentPage: 1,
  feeds: [], //페이지 변경 마다 데이터를 가져오기 때문에 줄여주기 위해 배열을 이용
};

// 공통요소로 네트워크로 api를 호출
class Api {
  // 내부 저장
  url: string;
  ajax: XMLHttpRequest;
  // 클래스는 초기화 과정이 필요 url, XMLHttpRequest
  // instance 객체에 저장
  constructor(url: string) {
    this.url = url;
    this.ajax = new XMLHttpRequest();
  }

  protected getRequest<AjaxResponse>(): AjaxResponse {
    this.ajax.open('GET', this.url, false); // 데이터를 동기적으로 가져옴
    this.ajax.send(); //데이터를 가져옴

    return JSON.parse(this.ajax.response); // json형식으로 변환
  }
}
// 클래스를 통해서 더 확실한 목적을 갖게됨
class NewsFeedApi extends Api {
  getData(): NewsFeed[] {
    return this.getRequest<NewsFeed[]>();
  }
}

class NewsDetailApi extends Api {
  getData(): NewsDetail {
    return this.getRequest<NewsDetail>();
  }
}

// Generic 리턴값을 여러개 같을 경우가 존재해 사용하는곳에서 타입가드를 해줘야하지만
// 제네릭을 통해 입력이 n개 유형일때 출력이 n개 유형인것을 표현 가능하다.
// T라는 형식의 데이터의 리턴 값을 받겠다고 사용하는 곳에서 정의 내릴 수 있다.
//네트워크를 통해 데이터를 가져와 객체로 변경
function getData<AjaxResponse>(url: string): AjaxResponse {
  ajax.open('GET', url, false); // 데이터를 동기적으로 가져옴
  ajax.send(); //데이터를 가져옴

  return JSON.parse(ajax.response); // json형식으로 변환
}

// 리스트에 읽음상태 추가
function makeFeeds(feeds: NewsFeed[]): NewsFeed[] {
  for (let i = 0; i < feeds.length; i++) {
    feeds[i].read = false;
  }
  return feeds;
}

function updateView(html: string): void {
  if (container != null) {
    container.innerHTML = html;
  } else {
    console.error('최상위 컨테이너가 없이 UI를 진행하지 못합니다.');
  }
}

// 글목록을 가져오는 함수
function newsFeed(): void {
  // class를 사용해 가독성을 올릴수 있다
  const api = new NewsFeedApi(NEWS_URL);

  let newsFeed: NewsFeed[] = store.feeds;
  const ul = document.createElement('ul'); //테그를 생성

  const newsList = [];

  let template = `
  <div class="bg-gray-600 min-h-screen">
      <div class="bg-white text-xl">
        <div class="mx-auto px-4">
          <div class="flex justify-between items-center py-6">
            <div class="flex justify-start">
              <h1 class="font-extrabold">Hacker News</h1>
            </div>
            <div class="items-center justify-end">
              <a href="#/page/{{__prev_page__}}" class="text-gray-500">
                Previous
              </a>
              <a href="#/page/{{__next_page__}}" class="text-gray-500 ml-4">
                Next
              </a>
            </div>
          </div> 
        </div>
      </div>
      <div class="p-4 text-2xl text-gray-700">
        {{__news_feed__}}        
      </div>
    </div>
  `;

  //store에 리스트를 생성하여 서버에서 한번만 불러오게끔
  if (newsFeed.length === 0) {
    newsFeed = store.feeds = makeFeeds(api.getData());
  }
  console.log(newsFeed[0]);

  for (let i = (store.currentPage - 1) * 10; i < store.currentPage * 10; i++) {
    newsList.push(`
    <div class="p-6 ${
      newsFeed[i].read ? 'bg-green-500' : 'bg-white'
    } mt-6 rounded-lg shadow-md transition-colors duration-500 hover:bg-green-100">
        <div class="flex">
          <div class="flex-auto">
            <a href="#/show/${newsFeed[i].id}">${newsFeed[i].title}</a>  
          </div>
          <div class="text-center text-sm">
            <div class="w-10 text-white bg-green-300 rounded-lg px-0 py-2">${
              newsFeed[i].comments_count
            }</div>
          </div>
        </div>
        <div class="flex mt-3">
          <div class="grid grid-cols-3 text-sm text-gray-500">
            <div><i class="fas fa-user mr-1"></i>${newsFeed[i].user}</div>
            <div><i class="fas fa-heart mr-1"></i>${newsFeed[i].points}</div>
            <div><i class="far fa-clock mr-1"></i>${newsFeed[i].time_ago}</div>
          </div>  
        </div>
      </div>    
  `);
  }

  template = template.replace('{{__news_feed__}}', newsList.join('')); // 마크업을 대체
  template = template.replace(
    '{{__prev_page__}}',
    String(store.currentPage > 1 ? store.currentPage - 1 : 1)
  );
  template = template.replace(
    '{{__next_page__}}',
    String(store.currentPage + 1)
  );

  updateView(template);
}

//읽는곳
function newsDetail(): void {
  const id = location.hash.substring(7); // 주소와 관련된 데이터를 전달 #을 제거한 값을 출력
  const api = new NewsDetailApi(CONTENT_URL.replace('@id', id));
  const newsContent = api.getData();

  let template = `
  <div class="bg-gray-600 min-h-screen pb-8">
  <div class="bg-white text-xl">
    <div class="mx-auto px-4">
      <div class="flex justify-between items-center py-6">
        <div class="flex justify-start">
          <h1 class="font-extrabold">Hacker News</h1>
        </div>
        <div class="items-center justify-end">
          <a href="#/page/${store.currentPage}" class="text-gray-500">
            <i class="fa fa-times"></i>
          </a>
        </div>
      </div>
    </div>
  </div>

  <div class="h-full border rounded-xl bg-white m-6 p-4 ">
    <h2>${newsContent.title}</h2>
    <div class="text-gray-400 h-20">
      ${newsContent.content}
    </div>

    {{__comments__}}

  </div>
</div>
  `;
  for (let i = 0; i < store.feeds.length; i++) {
    if (store.feeds[i].id === Number(id)) {
      store.feeds[i].read = true;
      break;
    }
  }
  updateView(
    template.replace('{{__comments__}}', makeComment(newsContent.comments))
  );
}

function makeComment(comments: NewsComment[]): string {
  const commentString = [];
  for (let i = 0; i < comments.length; i++) {
    const comment: NewsComment = comments[i];

    commentString.push(`
      <div style="padding-left: ${comment.level * 40}px;" class="mt-4">
        <div class="text-gray-400">
          <i class="fa fa-sort-up mr-2"></i>
          <strong>${comment.user}</strong> ${comment.time_ago}
        </div>
        <p class="text-gray-700">${comment.content}</p>
      </div>      
    `);

    // 대댓글을 가져옴
    if (comments[i].comments.length > 0) {
      commentString.push(makeComment(comments[i].comments)); // 재귀 호출 하위의 comment가 없을때까지 호출
    }
  }

  return commentString.join('');
}

// 화면전환 location.hash에서 #은 빈값을 반환함
function router(): void {
  const routePath = location.hash;
  if (routePath === '') {
    newsFeed();
  } else if (routePath.indexOf('#/page/') >= 0) {
    store.currentPage = Number(routePath.substring(7));
    //indexOf 입력 문자열을 찾아 없으면 -1 값 아니면 위치 값을 전달
    newsFeed();
  } else {
    newsDetail();
  }
}

// #은 해시 해시가 바뀌었을때 이벤트가 발생 hashchange 이벤트를 사용(window에서 발생)

window.addEventListener('hashchange', router);
router();
